CREATE TABLE IF NOT EXISTS Games (id TEXT PRIMARY KEY NOT NULL, turn INTEGER NOT NULL DEFAULT 0, deck TEXT NOT NULL, players INTEGER NOT NULL DEFAULT 1, played_card INTEGER NOT NULL DEFAULT -1, player_pick INTEGER NOT NULL DEFAULT -1);
